function Notify(value, address) local childListIndex = 1 local childList = {}
childCount = Component.Call("ChildCount") - 1 if value == Component.Messages.LClickUp then for n=0,childCount do string = "Children["..n.."].Address" if Component.Call(string) == address then childHit = n Component.Call("Children["..n.."].Children[0].PropogateVisibility", true)
else childList[childListIndex] = n childListIndex = childListIndex + 1 Component.Call("Children["..n.."].Children[0].PropogateVisibility", false) end end childList[childListIndex] = childHit Component.Call("ReorderChildren", childList) elseif value == Component.Messages.Move then local posX, posY = Component.Call("Position") local nextX = posX for n=0,childCount do Component.Call("Children["..n.."].MoveTo", nextX, posY) nextX = nextX + Component.Call("Children["..n.."].Width") - 10 height = Component.Call("Children["..n.."].Height") Component.Call("Children["..n.."].Children[0].MoveTo", posX, posY + height) end end end
