local this = UIComponent(Address)
function ShowMenu(menu) this:PropagateVisibility(false) this:SetVisible(true)
Component.Call(menu..".PropogateVisibility", true) end
local this = UIComponent(Address)
function HideMenu(menu) this:PropagateVisibility(false) this:SetVisible(true)
Component.Call(menu..".PropogateVisibility", true) end

function OnSelect() local this = UIComponent(Address)
if this:CurrentState() ~= "inactive" then local panel = UIComponent(this:Parent("sp_episodic_campaign")) panel:LuaCall("SelectCampaign", 1) Component.Call("Parent.LuaCall", "Notify", Component.Messages.StateChange, Component.Call("CurrentState"), Address) end end
function SetState(state) Component.SetState(state) end

function OnSelect() local this = UIComponent(Address)
if this:CurrentState() ~= "inactive" then local panel = UIComponent(this:Parent("sp_episodic_campaign")) panel:LuaCall("SelectCampaign", 2) Component.Call("Parent.LuaCall", "Notify", Component.Messages.StateChange, Component.Call("CurrentState"), Address) end end
function SetState(state) Component.SetState(state) end

local origX, origY = Component.Call("Position") local sliderLength = Component.Call("Parent.Height") - Component.Call("Height") local sliderMin = Component.GetProperty("Parent.minValue") local sliderMax = Component.GetProperty("Parent.maxValue")
function Reset() sliderMin = Component.GetProperty("Parent.minValue") sliderMax = Component.GetProperty("Parent.maxValue")
origX, origY = Component.Call("Position") end
function HandleMove() local unused, newY = Component.Call("Position") local sliderX, sliderY = Component.Call("Parent.Position")
if (newY < sliderY) then newY = sliderY else if (newY > sliderY + sliderLength) then newY = sliderY + sliderLength end end Component.Call("MoveTo", origX, newY)
local value = (sliderMax - sliderMin) * ((newY - sliderY) / sliderLength) Component.SetProperty("Parent.Value", value)
Component.Call("Parent.LuaCall", "Update") end
function LimitHandle(newX, newY) x,y = Component.Call("Position")
return x, newY end
